import React, { useState } from 'react';

const SimpleUserForm: React.FC = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:3001/api/users', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name, email, password }),
      });

      if (response.ok) {
        setMessage('Usuário criado com sucesso!');
        setName('');
        setEmail('');
        setPassword('');
      } else {
        setMessage('Erro ao criar usuário');
      }
    } catch (error) {
      setMessage('Erro de conexão');
    }
  };

  return (
    <div style={{ padding: '20px', maxWidth: '500px', margin: '0 auto' }}>
      <h2>Criar Usuário</h2>

      {message && (
        <div style={{ 
          padding: '10px', 
          marginBottom: '20px', 
          backgroundColor: message.includes('sucesso') ? '#d4edda' : '#f8d7da',
          border: '1px solid #ccc',
          borderRadius: '4px'
        }}>
          {message}
        </div>
      )}

      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: '20px' }}>
          <label style={{ display: 'block', marginBottom: '5px', fontSize: '16px', fontWeight: 'bold' }}>
            Nome:
          </label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
            style={{
              width: '100%',
              padding: '15px',
              fontSize: '16px',
              border: '2px solid #ddd',
              borderRadius: '5px',
              outline: 'none',
              pointerEvents: 'auto',
              cursor: 'text',
              backgroundColor: 'white',
              zIndex: 10,
              position: 'relative',
              userSelect: 'text',
              WebkitUserSelect: 'text'
            }}
            placeholder="Digite seu nome"
          />
        </div>

        <div style={{ marginBottom: '20px' }}>
          <label style={{ display: 'block', marginBottom: '5px', fontSize: '16px', fontWeight: 'bold' }}>
            Email:
          </label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            style={{
              width: '100%',
              padding: '15px',
              fontSize: '16px',
              border: '2px solid #ddd',
              borderRadius: '5px',
              outline: 'none',
              pointerEvents: 'auto',
              cursor: 'text',
              backgroundColor: 'white',
              zIndex: 10,
              position: 'relative',
              userSelect: 'text',
              WebkitUserSelect: 'text'
            }}
            placeholder="Digite seu email"
          />
        </div>

        <div style={{ marginBottom: '20px' }}>
          <label style={{ display: 'block', marginBottom: '5px', fontSize: '16px', fontWeight: 'bold' }}>
            Senha:
          </label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            minLength={5}
            style={{
              width: '100%',
              padding: '15px',
              fontSize: '16px',
              border: '2px solid #ddd',
              borderRadius: '5px',
              outline: 'none',
              pointerEvents: 'auto',
              cursor: 'text',
              backgroundColor: 'white',
              zIndex: 10,
              position: 'relative',
              userSelect: 'text',
              WebkitUserSelect: 'text'
            }}
            placeholder="Digite sua senha (mín. 5 caracteres)"
          />
        </div>

        <button
          type="submit"
          style={{
            width: '100%',
            padding: '15px',
            fontSize: '18px',
            backgroundColor: '#007bff',
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            pointerEvents: 'auto',
            zIndex: 10,
            position: 'relative',
            userSelect: 'none'
          }}
        >
          Criar Usuário
        </button>
      </form>
    </div>
  );
};

export default SimpleUserForm;
