import React, { useState, useEffect } from 'react';
import SimpleUserForm from '../components/SimpleUserForm';

interface User {
  id: string;
  name: string;
  email: string;
  createdAt: string;
}

interface EditUserFormProps {
  user: User;
  onSave: (id: string, userData: { name: string; email: string; password?: string }) => void;
  onCancel: () => void;
}

const EditUserForm: React.FC<EditUserFormProps> = ({ user, onSave, onCancel }) => {
  const [name, setName] = useState(user.name);
  const [email, setEmail] = useState(user.email);
  const [password, setPassword] = useState('');

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!name.trim() || !email.trim()) {
      alert('Nome e email s√£o obrigat√≥rios');
      return;
    }

    if (password && password.length < 5) {
      alert('Se informada, a senha deve ter pelo menos 5 caracteres');
      return;
    }

    const userData: { name: string; email: string; password?: string } = {
      name: name.trim(),
      email: email.trim()
    };

    if (password.trim()) {
      userData.password = password;
    }

    onSave(user.id, userData);
  };

  return (
    <div 
      style={{
        border: '2px solid #007bff',
        borderRadius: '5px',
        padding: '20px',
        marginBottom: '10px',
        backgroundColor: '#f8f9ff'
      }}
    >
      <h4 style={{ margin: '0 0 15px 0', color: '#007bff' }}>‚úèÔ∏è Editando usu√°rio</h4>
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: '15px' }}>
          <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>
            Nome:
          </label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
            style={{
              width: '100%',
              padding: '10px',
              fontSize: '16px',
              border: '2px solid #ddd',
              borderRadius: '4px',
              outline: 'none'
            }}
          />
        </div>

        <div style={{ marginBottom: '15px' }}>
          <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>
            Email:
          </label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            style={{
              width: '100%',
              padding: '10px',
              fontSize: '16px',
              border: '2px solid #ddd',
              borderRadius: '4px',
              outline: 'none'
            }}
          />
        </div>

        <div style={{ marginBottom: '20px' }}>
          <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>
            Nova Senha (opcional):
          </label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Deixe em branco para manter a senha atual"
            style={{
              width: '100%',
              padding: '10px',
              fontSize: '16px',
              border: '2px solid #ddd',
              borderRadius: '4px',
              outline: 'none'
            }}
          />
        </div>

        <div style={{ display: 'flex', gap: '10px' }}>
          <button
            type="submit"
            style={{
              backgroundColor: '#28a745',
              color: 'white',
              border: 'none',
              padding: '12px 20px',
              borderRadius: '4px',
              cursor: 'pointer',
              fontSize: '16px'
            }}
          >
            üíæ Salvar
          </button>
          <button
            type="button"
            onClick={onCancel}
            style={{
              backgroundColor: '#6c757d',
              color: 'white',
              border: 'none',
              padding: '12px 20px',
              borderRadius: '4px',
              cursor: 'pointer',
              fontSize: '16px'
            }}
          >
            ‚ùå Cancelar
          </button>
        </div>
      </form>
    </div>
  );
};

const SimplePage: React.FC = () => {
  const [users, setUsers] = useState<User[]>([]);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState<'success' | 'error' | ''>('');
  const [editingUser, setEditingUser] = useState<User | null>(null);

  const showMessage = (text: string, type: 'success' | 'error') => {
    setMessage(text);
    setMessageType(type);
    setTimeout(() => {
      setMessage('');
      setMessageType('');
    }, 3000);
  };

  const fetchUsers = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/users');
      const data = await response.json();
      setUsers(data);
    } catch (error) {
      console.error('Erro ao buscar usu√°rios:', error);
    } finally {
      setLoading(false);
    }
  };

  const deleteUser = async (id: string, userName: string) => {
    // Confirma√ß√£o antes de excluir
    const confirmDelete = window.confirm(
      `üóëÔ∏è Tem certeza que deseja excluir o usu√°rio "${userName}"?\n\nEsta a√ß√£o n√£o pode ser desfeita.`
    );

    if (!confirmDelete) {
      return;
    }

    try {
      const response = await fetch(`http://localhost:3001/api/users/${id}`, {
        method: 'DELETE'
      });

      if (response.ok) {
        showMessage(`‚úÖ Usu√°rio "${userName}" foi exclu√≠do com sucesso!`, 'success');
        fetchUsers();
      } else {
        throw new Error('Erro na resposta do servidor');
      }
    } catch (error) {
      console.error('Erro ao deletar usu√°rio:', error);
      showMessage(`‚ùå Erro ao excluir usu√°rio "${userName}". Tente novamente.`, 'error');
    }
  };

  const updateUser = async (id: string, userData: { name: string; email: string; password?: string }) => {
    try {
      const response = await fetch(`http://localhost:3001/api/users/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(userData),
      });

      if (response.ok) {
        showMessage(`‚úÖ Usu√°rio "${userData.name}" foi atualizado com sucesso!`, 'success');
        setEditingUser(null);
        fetchUsers();
      } else {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Erro na resposta do servidor');
      }
    } catch (error) {
      console.error('Erro ao atualizar usu√°rio:', error);
      showMessage(`‚ùå Erro ao atualizar usu√°rio. Tente novamente.`, 'error');
    }
  };

  const startEdit = (user: User) => {
    setEditingUser(user);
  };

  const cancelEdit = () => {
    setEditingUser(null);
  };

  useEffect(() => {
    fetchUsers();
    // Atualizar lista a cada 3 segundos
    const interval = setInterval(fetchUsers, 3000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{ padding: '20px', maxWidth: '800px', margin: '0 auto' }}>
      <h1 style={{ textAlign: 'center', color: '#333' }}>
        Sistema de Cadastro de Usu√°rios
      </h1>

      {/* √Årea de Mensagens */}
      {message && (
        <div 
          style={{
            padding: '15px',
            borderRadius: '5px',
            marginBottom: '20px',
            textAlign: 'center',
            fontSize: '16px',
            fontWeight: 'bold',
            backgroundColor: messageType === 'success' ? '#d4edda' : '#f8d7da',
            color: messageType === 'success' ? '#155724' : '#721c24',
            border: messageType === 'success' ? '1px solid #c3e6cb' : '1px solid #f5c6cb'
          }}
        >
          {message}
        </div>
      )}

      <SimpleUserForm />

      <div style={{ marginTop: '40px' }}>
        <h2>Usu√°rios Cadastrados ({users.length})</h2>

        {loading ? (
          <p>Carregando usu√°rios...</p>
        ) : users.length === 0 ? (
          <p>Nenhum usu√°rio cadastrado.</p>
        ) : (
          <div>
            {users.map(user => (
              <div key={user.id}>
                {editingUser && editingUser.id === user.id ? (
                  <EditUserForm 
                    user={editingUser}
                    onSave={updateUser}
                    onCancel={cancelEdit}
                  />
                ) : (
                  <div 
                    style={{
                      border: '1px solid #ddd',
