import React, { useState, useEffect } from 'react';
import SimpleUserForm from '../components/SimpleUserForm';

interface User {
  id: string;
  name: string;
  email: string;
  createdAt: string;
}

const SimplePage: React.FC = () => {
  const [users, setUsers] = useState<User[]>([]);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState<'success' | 'error' | ''>('');

  const showMessage = (text: string, type: 'success' | 'error') => {
    setMessage(text);
    setMessageType(type);
    setTimeout(() => {
      setMessage('');
      setMessageType('');
    }, 3000);
  };

  const fetchUsers = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/users');
      const data = await response.json();
      setUsers(data);
    } catch (error) {
      console.error('Erro ao buscar usuários:', error);
    } finally {
      setLoading(false);
    }
  };

  const deleteUser = async (id: string, userName: string) => {
    // Confirmação antes de excluir
    const confirmDelete = window.confirm(
      `🗑️ Tem certeza que deseja excluir o usuário "${userName}"?\n\nEsta ação não pode ser desfeita.`
    );

    if (!confirmDelete) {
      return;
    }

    try {
      const response = await fetch(`http://localhost:3001/api/users/${id}`, {
        method: 'DELETE'
      });

      if (response.ok) {
        showMessage(`✅ Usuário "${userName}" foi excluído com sucesso!`, 'success');
        fetchUsers();
      } else {
        throw new Error('Erro na resposta do servidor');
      }
    } catch (error) {
      console.error('Erro ao deletar usuário:', error);
      showMessage(`❌ Erro ao excluir usuário "${userName}". Tente novamente.`, 'error');
    }
  };

  useEffect(() => {
    fetchUsers();
    // Atualizar lista a cada 3 segundos
    const interval = setInterval(fetchUsers, 3000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{ padding: '20px', maxWidth: '800px', margin: '0 auto' }}>
      <h1 style={{ textAlign: 'center', color: '#333' }}>
        Sistema de Cadastro de Usuários
      </h1>

      {/* Área de Mensagens */}
      {message && (
        <div 
          style={{
            padding: '15px',
            borderRadius: '5px',
            marginBottom: '20px',
            textAlign: 'center',
            fontSize: '16px',
            fontWeight: 'bold',
            backgroundColor: messageType === 'success' ? '#d4edda' : '#f8d7da',
            color: messageType === 'success' ? '#155724' : '#721c24',
            border: messageType === 'success' ? '1px solid #c3e6cb' : '1px solid #f5c6cb'
          }}
        >
          {message}
        </div>
      )}

      <SimpleUserForm />

      <div style={{ marginTop: '40px' }}>
        <h2>Usuários Cadastrados ({users.length})</h2>

        {loading ? (
          <p>Carregando usuários...</p>
        ) : users.length === 0 ? (
          <p>Nenhum usuário cadastrado.</p>
        ) : (
          <div>
            {users.map(user => (
              <div 
                key={user.id}
                style={{
                  border: '1px solid #ddd',
                  borderRadius: '5px',
                  padding: '15px',
                  marginBottom: '10px',
                  backgroundColor: '#f9f9f9',
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'center'
                }}
              >
                <div>
                  <h3 style={{ margin: '0 0 5px 0' }}>{user.name}</h3>
                  <p style={{ margin: '0', color: '#666' }}>{user.email}</p>
                  <small style={{ color: '#888' }}>
                    ID: {user.id} | Criado: {new Date(user.createdAt).toLocaleString('pt-BR')}
                  </small>
                </div>
                <button
                  onClick={() => deleteUser(user.id, user.name)}
                  style={{
                    backgroundColor: '#dc3545',
                    color: 'white',
                    border: 'none',
                    padding: '10px 15px',
                    borderRadius: '4px',
                    cursor: 'pointer'
                  }}
                >
                  🗑️ Excluir
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default SimplePage;
